# Blue-Green Deployment Configuration
# This file is managed by CI/CD - do not edit manually

# DNS resolver for dynamic upstream resolution
# This allows nginx to start even if upstreams don't exist yet
resolver 127.0.0.11 valid=10s ipv6=off;
resolver_timeout 5s;

# Active environment marker (updated by CI/CD)
# ACTIVE_ENVIRONMENT=blue

# Main frontend proxy (port 443)
server {
    listen 80;
    listen 443 ssl http2;
    server_name localhost;

    # Enable dynamic upstream resolution
    set $backend_blue "echobase-blue-api-gateway:3001";
    set $frontend_blue "echobase-blue-frontend:443";

    ssl_certificate /etc/ssl/localhost.crt;
    ssl_certificate_key /etc/ssl/localhost.key;

    # Security headers
    add_header X-Frame-Options "DENY" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header Referrer-Policy "strict-origin-when-cross-origin" always;
    add_header Strict-Transport-Security "max-age=31536000; includeSubDomains" always;

    # Active environment endpoint - returns which environment is currently active
    # Returns "none" in bootstrap state (no environment promoted yet)
    # Updated by CI/CD when traffic is switched
    location = /.active-env {
        default_type text/plain;
        return 200 "none";
    }

    # Route to active environment (blue by default)
    # This will be updated by CI/CD automation
    location / {
        proxy_pass https://$frontend_blue;
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection 'upgrade';
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }

    location /api/ {
        proxy_pass https://$backend_blue;
        proxy_http_version 1.1;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }

    # Health check endpoint
    location /health {
        proxy_pass https://$backend_blue/health;
        proxy_ssl_verify off;
    }

    # Observability UIs — basic auth protected
    location /prometheus/ {
        auth_basic "Observability";
        auth_basic_user_file /etc/nginx/.htpasswd;
        proxy_pass http://prometheus:9090/;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
    }

    location /jaeger/ {
        auth_basic "Observability";
        auth_basic_user_file /etc/nginx/.htpasswd;
        proxy_pass http://jaeger:16686/;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
    }
}

# Blue environment direct access (for testing)
server {
    listen 8080;
    server_name localhost;

    # Variables for blue environment
    set $backend_blue_direct "echobase-blue-api-gateway:3001";
    set $frontend_blue_direct "echobase-blue-frontend:443";

    # Security headers
    add_header X-Frame-Options "DENY" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header Referrer-Policy "strict-origin-when-cross-origin" always;

    location / {
        proxy_pass https://$frontend_blue_direct;
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection 'upgrade';
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }

    location /api/ {
        proxy_pass https://$backend_blue_direct;
        proxy_http_version 1.1;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }
}

# Green environment direct access (for testing)
server {
    listen 8081;
    server_name localhost;

    # Variables for green environment
    set $backend_green_direct "echobase-green-api-gateway:3001";
    set $frontend_green_direct "echobase-green-frontend:443";

    # Security headers
    add_header X-Frame-Options "DENY" always;
    add_header X-Content-Type-Options "nosniff" always;
    add_header Referrer-Policy "strict-origin-when-cross-origin" always;

    location / {
        proxy_pass https://$frontend_green_direct;
        proxy_http_version 1.1;
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection 'upgrade';
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }

    location /api/ {
        proxy_pass https://$backend_green_direct;
        proxy_http_version 1.1;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_ssl_verify off;
    }
}

