  1→# Blue-Green Deployment Configuration
  2→# This file is managed by CI/CD - do not edit manually
  3→
  4→# DNS resolver for dynamic upstream resolution
  5→# This allows nginx to start even if upstreams don't exist yet
  6→resolver 127.0.0.11 valid=10s ipv6=off;
  7→resolver_timeout 5s;
  8→
  9→# Active environment marker (updated by CI/CD)
 10→# ACTIVE_ENVIRONMENT=blue
 11→
 12→# Main frontend proxy (port 443)
 13→server {
 14→    listen 80;
 15→    listen 443 ssl http2;
 16→    server_name localhost;
 17→
 18→    # Enable dynamic upstream resolution
 19→    set $backend_blue "echobase-blue-api-gateway:3001";
 20→    set $frontend_blue "echobase-blue-frontend:443";
 21→
 22→    ssl_certificate /etc/ssl/localhost.crt;
 23→    ssl_certificate_key /etc/ssl/localhost.key;
 24→
 25→    # Security headers
 26→    add_header X-Frame-Options "DENY" always;
 27→    add_header X-Content-Type-Options "nosniff" always;
 28→    add_header Referrer-Policy "strict-origin-when-cross-origin" always;
 29→    add_header Strict-Transport-Security "max-age=31536000; includeSubDomains" always;
 30→
 31→    # Active environment endpoint - returns which environment is currently active
 32→    # Returns "none" in bootstrap state (no environment promoted yet)
 33→    # Updated by CI/CD when traffic is switched
 34→    location = /.active-env {
 35→        default_type text/plain;
 36→        return 200 "none";
 37→    }
 38→
 39→    # Route to active environment (blue by default)
 40→    # This will be updated by CI/CD automation
 41→    location / {
 42→        proxy_pass https://$frontend_blue;
 43→        proxy_http_version 1.1;
 44→        proxy_set_header Upgrade $http_upgrade;
 45→        proxy_set_header Connection 'upgrade';
 46→        proxy_set_header Host $host;
 47→        proxy_set_header X-Real-IP $remote_addr;
 48→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
 49→        proxy_set_header X-Forwarded-Proto $scheme;
 50→        proxy_ssl_verify off;
 51→    }
 52→
 53→    location /api/ {
 54→        proxy_pass https://$backend_blue;
 55→        proxy_http_version 1.1;
 56→        proxy_set_header Host $host;
 57→        proxy_set_header X-Real-IP $remote_addr;
 58→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
 59→        proxy_set_header X-Forwarded-Proto $scheme;
 60→        proxy_ssl_verify off;
 61→    }
 62→
 63→    # Health check endpoint
 64→    location /health {
 65→        proxy_pass https://$backend_blue/health;
 66→        proxy_ssl_verify off;
 67→    }
 68→
 69→    # Observability UIs — basic auth protected
 70→    # Prometheus and Jaeger are configured with their subpath as their base URL
 71→    # (--web.external-url=/prometheus/ and QUERY_BASE_PATH=/jaeger respectively),
 72→    # so nginx forwards the prefix as-is (no trailing / on proxy_pass to avoid stripping).
 73→    location /prometheus/ {
 74→        auth_basic "Observability";
 75→        auth_basic_user_file /etc/nginx/.htpasswd;
 76→        proxy_pass http://prometheus:9090;
 77→        proxy_set_header Host $host;
 78→        proxy_set_header X-Real-IP $remote_addr;
 79→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
 80→        proxy_set_header X-Forwarded-Proto $scheme;
 81→    }
 82→
 83→    location /jaeger/ {
 84→        auth_basic "Observability";
 85→        auth_basic_user_file /etc/nginx/.htpasswd;
 86→        proxy_pass http://jaeger:16686;
 87→        proxy_set_header Host $host;
 88→        proxy_set_header X-Real-IP $remote_addr;
 89→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
 90→        proxy_set_header X-Forwarded-Proto $scheme;
 91→    }
 92→}
 93→
 94→# Blue environment direct access (for testing)
 95→server {
 96→    listen 8080;
 97→    server_name localhost;
 98→
 99→    # Variables for blue environment
100→    set $backend_blue_direct "echobase-blue-api-gateway:3001";
101→    set $frontend_blue_direct "echobase-blue-frontend:443";
102→
103→    # Security headers
104→    add_header X-Frame-Options "DENY" always;
105→    add_header X-Content-Type-Options "nosniff" always;
106→    add_header Referrer-Policy "strict-origin-when-cross-origin" always;
107→
108→    location / {
109→        proxy_pass https://$frontend_blue_direct;
110→        proxy_http_version 1.1;
111→        proxy_set_header Upgrade $http_upgrade;
112→        proxy_set_header Connection 'upgrade';
113→        proxy_set_header Host $host;
114→        proxy_set_header X-Real-IP $remote_addr;
115→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
116→        proxy_set_header X-Forwarded-Proto $scheme;
117→        proxy_ssl_verify off;
118→    }
119→
120→    location /api/ {
121→        proxy_pass https://$backend_blue_direct;
122→        proxy_http_version 1.1;
123→        proxy_set_header Host $host;
124→        proxy_set_header X-Real-IP $remote_addr;
125→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
126→        proxy_set_header X-Forwarded-Proto $scheme;
127→        proxy_ssl_verify off;
128→    }
129→}
130→
