149→        proxy_set_header Connection 'upgrade';
150→        proxy_set_header Host $host;
151→        proxy_set_header X-Real-IP $remote_addr;
152→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
153→        proxy_set_header X-Forwarded-Proto $scheme;
154→        proxy_ssl_verify off;
155→    }
156→
157→    location /api/ {
158→        proxy_pass https://$backend_green_direct;
159→        proxy_http_version 1.1;
160→        proxy_set_header Host $host;
161→        proxy_set_header X-Real-IP $remote_addr;
162→        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
